// Sorin Jayaweera // 8/29/2025 // 12:30 am // sojayaweera@g.hmc.edu
module testbench(); logic reset; logic clk; logic [3:0] s; logic [2:0] led; logic [6:0] seg;
// expected output parameters
logic [2:0] eled;
logic [6:0] eseg;

int vectornum, errors;
// change this bit width depending on the testvectors you are reading 
logic [16:0] testvectors[15:0];

// change this to whatever module - top, ssegdisp, or light_status
top dut(.s(s), .reset(reset), .led(led), .seg(seg));

// generate clock
always 
    begin
      clk = 1; #5; clk = 0; #5;
    end


initial begin

    $display(" reading ");
    $readmemb("testvectors.tv", testvectors); // change to the testvector name you are reading
    vectornum = 0; errors = 0;
    reset = 1; #23; reset = 0;

    $display(" finished read");
    end

 always @(posedge clk) 
     begin
        #1; {s,eled,eseg} = testvectors[vectornum]; // change this to what you want: s and eled, s and eseg, or all three
        $display(" %d %d %d test %d", s,eled,eseg,vectornum);
     end 
     // check results on falling edge of clk
 always @(negedge clk) 
     if (~reset) begin // skip during reset
        if ({led,seg} !== {eled,eseg}) begin // change this to be the thing you want to test - led, seg, or both
            errors = errors + 1; // GOOD
        end
     vectornum = vectornum + 1;
     if (testvectors[vectornum] === 14'bx) begin //Change this to match the bitwidth
         $display(" %d tests completed with %d errors", vectornum, errors); 
         $stop; 
     end 
end
endmodule

