// Sorin Jayaweera // 909 957 6074 // sojayaweera@g.hmc.edu // 9/6/2025
module top( input logic [3:0] i0, input logic [3:0] i1, input logic reset, output logic sel, output logic nsel, output logic [6:0] segout, output logic [4:0] sum );
HSOSC hf_osc (.CLKHFPU(1'b1), .CLKHFEN(1'b1), .CLKHF(int_osc));

logic [3:0] iActive;

logic [24:0] counter = 0;

// Look up table for the 7 segment displays
segLUT lut(.s(iActive),.seg(segout));

// add the inputs and display on LEDS
assign sum = i1+i0;

// choosing which set of connections for the resource use
// sequential logic
always_ff@(posedge int_osc) begin

        if(reset == 1) begin
                iActive &lt;= 4'b0000;
            end
        else begin
            iActive &lt;= sel ? i0 : i1;
            end

end

// select as a slow clock logic
always_ff@(posedge int_osc) begin
    if(reset) begin
            counter &lt;= 25'b0;
        end
    else begin
        counter &lt;= counter + 1'b1;
    end
end

assign sel = counter[18];// // 1.43 hz
assign nsel = !sel;
endmodule


