// Sorin Jayaweera // sojayaweera@g.hmc.edu // 8/28/2025
module lab1_sj_v1( input logic [3:0] s, input logic reset, output logic [2:0] led, output logic [6:0] seg );
light_status ls(.s(s),.reset(reset),.led(led));
ssegdisp sdisp(.s(s),.seg(seg));
endmodule
// Control the onboard LEDS with input from the user. // one led will blink at 2.4 hz, // the other leds are status module light_status ( input logic [3:0] s, input logic reset, output logic [2:0] led ); logic lastled = 0; logic [24:0] counter = 0;
assign led[0] = s[0] ^ s[1];
assign led[1] = s[3] & s[2];

// 24 Mhz Internal high-speed oscillator
HSOSC hf_osc (.CLKHFPU(1'b1), .CLKHFEN(1'b1), .CLKHF(int_osc));

assign led[2] = counter % 2000000 &lt; 100000; // every million counts, blink led.  

// Simple clock divider copied
always_ff @(posedge int_osc,negedge reset)
begin
    if (reset == 0) begin 
        counter &lt;= 24'b0;
        end

    else begin
        counter &lt;= counter + 1'b1;
    end
end
endmodule
// take the user unput from s as a number 0-9 and push to a seven segment display {ABCDEFG} module ssegdisp( input logic [3:0] s, output logic [6:0] seg );
always_comb
    case(s)
        4'b0000: seg &lt;= 7'b0000001; //7'b1111110; 0000001
        4'b0001: seg &lt;= 7'b1001111; //7'b0110000; 1001111
        4'b0010: seg &lt;= 7'b0010010; //7'b1101101; 0010010
        4'b0011: seg &lt;= 7'b0000110; //7'b1111001; 0000110
        4'b0100: seg &lt;= 7'b1001100; //7'b0110011; 1001100
        4'b0101: seg &lt;= 7'b0100100; //7'b1011011; 0100100
        4'b0110: seg &lt;= 7'b1100000; //7'b0011111; 1100000
        4'b0111: seg &lt;= 7'b0001111; //7'b1110000; 0001111
        4'b1000: seg &lt;= 7'b0000000; //7'b1111111; 0000000
        4'b1001: seg &lt;= 7'b0001100; //7'b1110011; 0001100
    default
        seg &lt;= 7'b1111111;//7'b0000000; 1111111
    endcase
endmodule
